#!/bin/bash
# Uninstall script for Swift Quit
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Swift Quit..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Swift Quit if running..."
pkill -f "Swift Quit" 2>/dev/null || true

# Kill application with bundle ID onebadidea.Swift-Quit if running
echo "Stopping application with bundle ID onebadidea.Swift-Quit if running..."
killall -9 "onebadidea.Swift-Quit" 2>/dev/null || true

# Remove /Applications/Swift Quit.app
echo "Removing /Applications/Swift Quit.app..."
if [ -d "/Applications/Swift Quit.app" ]; then
    rm -rf "/Applications/Swift Quit.app" 2>/dev/null || true
elif [ -f "/Applications/Swift Quit.app" ]; then
    rm -f "/Applications/Swift Quit.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/onebadidea.Swift-Quit.plist
echo "Removing $HOME/Library/Preferences/onebadidea.Swift-Quit.plist..."
if [ -d "$HOME/Library/Preferences/onebadidea.Swift-Quit.plist" ]; then
    rm -rf "$HOME/Library/Preferences/onebadidea.Swift-Quit.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/onebadidea.Swift-Quit.plist" ]; then
    rm -f "$HOME/Library/Preferences/onebadidea.Swift-Quit.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
