#!/bin/bash
# Uninstall script for swiftDialog
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling swiftDialog..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping swiftDialog if running..."
pkill -f "swiftDialog" 2>/dev/null || true

# Remove /Library/Application Support/Dialog
echo "Removing /Library/Application Support/Dialog..."
if [ -d "/Library/Application Support/Dialog" ]; then
    rm -rf "/Library/Application Support/Dialog" 2>/dev/null || true
elif [ -f "/Library/Application Support/Dialog" ]; then
    rm -f "/Library/Application Support/Dialog" 2>/dev/null || true
fi

# Remove /Library/Preferences/au.csiro.dialog.plist
echo "Removing /Library/Preferences/au.csiro.dialog.plist..."
if [ -d "/Library/Preferences/au.csiro.dialog.plist" ]; then
    rm -rf "/Library/Preferences/au.csiro.dialog.plist" 2>/dev/null || true
elif [ -f "/Library/Preferences/au.csiro.dialog.plist" ]; then
    rm -f "/Library/Preferences/au.csiro.dialog.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
