#!/bin/bash
# Uninstall script for Swifty
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Swifty..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Swifty if running..."
pkill -f "Swifty" 2>/dev/null || true

# Remove /Applications/Swifty.app
echo "Removing /Applications/Swifty.app..."
if [ -d "/Applications/Swifty.app" ]; then
    rm -rf "/Applications/Swifty.app" 2>/dev/null || true
elif [ -f "/Applications/Swifty.app" ]; then
    rm -f "/Applications/Swifty.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Swifty
echo "Removing $HOME/Library/Application Support/Swifty..."
if [ -d "$HOME/Library/Application Support/Swifty" ]; then
    rm -rf "$HOME/Library/Application Support/Swifty" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Swifty" ]; then
    rm -f "$HOME/Library/Application Support/Swifty" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Swifty
echo "Removing $HOME/Library/Logs/Swifty..."
if [ -d "$HOME/Library/Logs/Swifty" ]; then
    rm -rf "$HOME/Library/Logs/Swifty" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Swifty" ]; then
    rm -f "$HOME/Library/Logs/Swifty" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.electron.swifty.plist
echo "Removing $HOME/Library/Preferences/com.electron.swifty.plist..."
