# Remove $HOME/Library/Saved Application State/com.electron.swifty.savedState
echo "Removing $HOME/Library/Saved Application State/com.electron.swifty.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.electron.swifty.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.electron.swifty.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.electron.swifty.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.electron.swifty.savedState" 2>/dev/null || true
