#!/bin/bash
# Uninstall script for Swish
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Swish..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Swish if running..."
pkill -f "Swish" 2>/dev/null || true

# Remove /Applications/Swish.app
echo "Removing /Applications/Swish.app..."
if [ -d "/Applications/Swish.app" ]; then
    rm -rf "/Applications/Swish.app" 2>/dev/null || true
elif [ -f "/Applications/Swish.app" ]; then
    rm -f "/Applications/Swish.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Swish
echo "Removing $HOME/Library/Application Support/Swish..."
if [ -d "$HOME/Library/Application Support/Swish" ]; then
    rm -rf "$HOME/Library/Application Support/Swish" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Swish" ]; then
    rm -f "$HOME/Library/Application Support/Swish" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/co.highlyopinionated.swish
echo "Removing $HOME/Library/Caches/co.highlyopinionated.swish..."
if [ -d "$HOME/Library/Caches/co.highlyopinionated.swish" ]; then
    rm -rf "$HOME/Library/Caches/co.highlyopinionated.swish" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/co.highlyopinionated.swish" ]; then
    rm -f "$HOME/Library/Caches/co.highlyopinionated.swish" 2>/dev/null || true
fi

# Remove $HOME/Library/Cookies/co.highlyopinionated.swish.binarycookies
echo "Removing $HOME/Library/Cookies/co.highlyopinionated.swish.binarycookies..."
if [ -d "$HOME/Library/Cookies/co.highlyopinionated.swish.binarycookies" ]; then
