elif [ -f "$HOME/Library/Cookies/co.highlyopinionated.swish.binarycookies" ]; then
    rm -f "$HOME/Library/Cookies/co.highlyopinionated.swish.binarycookies" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/co.highlyopinionated.swish.plist
echo "Removing $HOME/Library/Preferences/co.highlyopinionated.swish.plist..."
if [ -d "$HOME/Library/Preferences/co.highlyopinionated.swish.plist" ]; then
    rm -rf "$HOME/Library/Preferences/co.highlyopinionated.swish.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/co.highlyopinionated.swish.plist" ]; then
    rm -f "$HOME/Library/Preferences/co.highlyopinionated.swish.plist" 2>/dev/null || true
fi

