#!/bin/bash
# Uninstall script for Tableau Public
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Tableau Public..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Tableau Public if running..."
pkill -f "Tableau Public" 2>/dev/null || true

# Remove $HOME/Library/Caches/com.tableau.caching
echo "Removing $HOME/Library/Caches/com.tableau.caching..."
if [ -d "$HOME/Library/Caches/com.tableau.caching" ]; then
    rm -rf "$HOME/Library/Caches/com.tableau.caching" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.tableau.caching" ]; then
    rm -f "$HOME/Library/Caches/com.tableau.caching" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.tableausoftware.MapTiles
echo "Removing $HOME/Library/Caches/com.tableausoftware.MapTiles..."
if [ -d "$HOME/Library/Caches/com.tableausoftware.MapTiles" ]; then
    rm -rf "$HOME/Library/Caches/com.tableausoftware.MapTiles" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.tableausoftware.MapTiles" ]; then
    rm -f "$HOME/Library/Caches/com.tableausoftware.MapTiles" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
