#!/bin/bash
# Uninstall script for Tabula
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Tabula..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Tabula if running..."
pkill -f "Tabula" 2>/dev/null || true

# Remove /Applications/tabula/Tabula.app
echo "Removing /Applications/tabula/Tabula.app..."
if [ -d "/Applications/tabula/Tabula.app" ]; then
    rm -rf "/Applications/tabula/Tabula.app" 2>/dev/null || true
elif [ -f "/Applications/tabula/Tabula.app" ]; then
    rm -f "/Applications/tabula/Tabula.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Tabula
echo "Removing $HOME/Library/Application Support/Tabula..."
if [ -d "$HOME/Library/Application Support/Tabula" ]; then
    rm -rf "$HOME/Library/Application Support/Tabula" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Tabula" ]; then
    rm -f "$HOME/Library/Application Support/Tabula" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.nerdpower.tabula.Tabula.savedState
echo "Removing $HOME/Library/Saved Application State/org.nerdpower.tabula.Tabula.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.nerdpower.tabula.Tabula.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.nerdpower.tabula.Tabula.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.nerdpower.tabula.Tabula.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.nerdpower.tabula.Tabula.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
