#!/bin/bash
# Uninstall script for TIDAL
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling TIDAL..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping TIDAL if running..."
pkill -f "TIDAL" 2>/dev/null || true

# Remove /Applications/TIDAL.app
echo "Removing /Applications/TIDAL.app..."
if [ -d "/Applications/TIDAL.app" ]; then
    rm -rf "/Applications/TIDAL.app" 2>/dev/null || true
elif [ -f "/Applications/TIDAL.app" ]; then
    rm -f "/Applications/TIDAL.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/TIDAL
echo "Removing $HOME/Library/Application Support/TIDAL..."
if [ -d "$HOME/Library/Application Support/TIDAL" ]; then
    rm -rf "$HOME/Library/Application Support/TIDAL" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/TIDAL" ]; then
    rm -f "$HOME/Library/Application Support/TIDAL" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.tidal.desktop
echo "Removing $HOME/Library/Caches/com.tidal.desktop..."
if [ -d "$HOME/Library/Caches/com.tidal.desktop" ]; then
    rm -rf "$HOME/Library/Caches/com.tidal.desktop" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.tidal.desktop" ]; then
    rm -f "$HOME/Library/Caches/com.tidal.desktop" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.tidal.desktop.ShipIt
echo "Removing $HOME/Library/Caches/com.tidal.desktop.ShipIt..."
if [ -d "$HOME/Library/Caches/com.tidal.desktop.ShipIt" ]; then
    rm -rf "$HOME/Library/Caches/com.tidal.desktop.ShipIt" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.tidal.desktop.ShipIt" ]; then
    rm -f "$HOME/Library/Caches/com.tidal.desktop.ShipIt" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/TIDAL
echo "Removing $HOME/Library/Logs/TIDAL..."
if [ -d "$HOME/Library/Logs/TIDAL" ]; then
    rm -rf "$HOME/Library/Logs/TIDAL" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/TIDAL" ]; then
    rm -f "$HOME/Library/Logs/TIDAL" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.tidal.*
echo "Removing $HOME/Library/Preferences/com.tidal.*..."
if [ -d "$HOME/Library/Preferences/com.tidal.*" ]; then
    rm -rf "$HOME/Library/Preferences/com.tidal.*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.tidal.*" ]; then
    rm -f "$HOME/Library/Preferences/com.tidal.*" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.tidal.desktop.savedState
echo "Removing $HOME/Library/Saved Application State/com.tidal.desktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.tidal.desktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.tidal.desktop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.tidal.desktop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.tidal.desktop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
