#!/bin/bash
# Uninstall script for TigerVNC
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling TigerVNC..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping TigerVNC if running..."
pkill -f "TigerVNC" 2>/dev/null || true

# Remove /Applications/TigerVNC.app
echo "Removing /Applications/TigerVNC.app..."
if [ -d "/Applications/TigerVNC.app" ]; then
    rm -rf "/Applications/TigerVNC.app" 2>/dev/null || true
elif [ -f "/Applications/TigerVNC.app" ]; then
    rm -f "/Applications/TigerVNC.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.tigervnc.tigervnc.savedState
echo "Removing $HOME/Library/Saved Application State/com.tigervnc.tigervnc.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.tigervnc.tigervnc.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.tigervnc.tigervnc.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.tigervnc.tigervnc.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.tigervnc.tigervnc.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
