#!/bin/bash
# Uninstall script for Timer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Timer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Timer if running..."
pkill -f "Timer" 2>/dev/null || true

# Remove /Applications/Timer.app
echo "Removing /Applications/Timer.app..."
if [ -d "/Applications/Timer.app" ]; then
    rm -rf "/Applications/Timer.app" 2>/dev/null || true
elif [ -f "/Applications/Timer.app" ]; then
    rm -f "/Applications/Timer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Apimac
echo "Removing $HOME/Library/Application Support/Apimac..."
if [ -d "$HOME/Library/Application Support/Apimac" ]; then
    rm -rf "$HOME/Library/Application Support/Apimac" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Apimac" ]; then
    rm -f "$HOME/Library/Application Support/Apimac" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Apimac
echo "Removing $HOME/Library/Preferences/Apimac..."
if [ -d "$HOME/Library/Preferences/Apimac" ]; then
    rm -rf "$HOME/Library/Preferences/Apimac" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Apimac" ]; then
    rm -f "$HOME/Library/Preferences/Apimac" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.apimac.Timer.plist
echo "Removing $HOME/Library/Preferences/com.apimac.Timer.plist..."
if [ -d "$HOME/Library/Preferences/com.apimac.Timer.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.apimac.Timer.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.apimac.Timer.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.apimac.Timer.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.apimac.TimerMac.pdata
echo "Removing $HOME/Library/Preferences/com.apimac.TimerMac.pdata..."
if [ -d "$HOME/Library/Preferences/com.apimac.TimerMac.pdata" ]; then
    rm -rf "$HOME/Library/Preferences/com.apimac.TimerMac.pdata" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.apimac.TimerMac.pdata" ]; then
    rm -f "$HOME/Library/Preferences/com.apimac.TimerMac.pdata" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
