#!/bin/bash
# Uninstall script for Tor Browser
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Tor Browser..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Tor Browser if running..."
pkill -f "Tor Browser" 2>/dev/null || true

# Remove /Applications/Tor Browser.app
echo "Removing /Applications/Tor Browser.app..."
if [ -d "/Applications/Tor Browser.app" ]; then
    rm -rf "/Applications/Tor Browser.app" 2>/dev/null || true
elif [ -f "/Applications/Tor Browser.app" ]; then
    rm -f "/Applications/Tor Browser.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.mozilla.tor browser.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.mozilla.tor browser.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.mozilla.tor browser.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.mozilla.tor browser.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.mozilla.tor browser.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.mozilla.tor browser.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/TorBrowser-Data
echo "Removing $HOME/Library/Application Support/TorBrowser-Data..."
if [ -d "$HOME/Library/Application Support/TorBrowser-Data" ]; then
    rm -rf "$HOME/Library/Application Support/TorBrowser-Data" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/TorBrowser-Data" ]; then
    rm -f "$HOME/Library/Application Support/TorBrowser-Data" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.mozilla.tor browser.plist
echo "Removing $HOME/Library/Preferences/org.mozilla.tor browser.plist..."
if [ -d "$HOME/Library/Preferences/org.mozilla.tor browser.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.mozilla.tor browser.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.mozilla.tor browser.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.mozilla.tor browser.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.torproject.torbrowser.plist
echo "Removing $HOME/Library/Preferences/org.torproject.torbrowser.plist..."
if [ -d "$HOME/Library/Preferences/org.torproject.torbrowser.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.torproject.torbrowser.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.torproject.torbrowser.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.torproject.torbrowser.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.torproject.torbrowser.savedState
echo "Removing $HOME/Library/Saved Application State/org.torproject.torbrowser.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.torproject.torbrowser.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.torproject.torbrowser.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.torproject.torbrowser.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.torproject.torbrowser.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
