#!/bin/bash
# Uninstall script for Tresorit
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Tresorit..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Tresorit if running..."
pkill -f "Tresorit" 2>/dev/null || true

# Remove /Applications/Tresorit.app
echo "Removing /Applications/Tresorit.app..."
if [ -d "/Applications/Tresorit.app" ]; then
    rm -rf "/Applications/Tresorit.app" 2>/dev/null || true
elif [ -f "/Applications/Tresorit.app" ]; then
    rm -f "/Applications/Tresorit.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Tresorit
echo "Removing $HOME/Library/Application Support/Tresorit..."
if [ -d "$HOME/Library/Application Support/Tresorit" ]; then
    rm -rf "$HOME/Library/Application Support/Tresorit" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Tresorit" ]; then
    rm -f "$HOME/Library/Application Support/Tresorit" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.tresorit.mac.plist
echo "Removing $HOME/Library/Preferences/com.tresorit.mac.plist..."
if [ -d "$HOME/Library/Preferences/com.tresorit.mac.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.tresorit.mac.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.tresorit.mac.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.tresorit.mac.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
