#!/bin/bash
# Uninstall script for Twitch Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Twitch Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Twitch Studio if running..."
pkill -f "Twitch Studio" 2>/dev/null || true

# Remove /Applications/Twitch Studio.app
echo "Removing /Applications/Twitch Studio.app..."
if [ -d "/Applications/Twitch Studio.app" ]; then
    rm -rf "/Applications/Twitch Studio.app" 2>/dev/null || true
elif [ -f "/Applications/Twitch Studio.app" ]; then
    rm -f "/Applications/Twitch Studio.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Twitch Studio
echo "Removing $HOME/Library/Application Support/Twitch Studio..."
if [ -d "$HOME/Library/Application Support/Twitch Studio" ]; then
    rm -rf "$HOME/Library/Application Support/Twitch Studio" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Twitch Studio" ]; then
    rm -f "$HOME/Library/Application Support/Twitch Studio" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/twitch-desktop-electron-platform
echo "Removing $HOME/Library/Application Support/twitch-desktop-electron-platform..."
if [ -d "$HOME/Library/Application Support/twitch-desktop-electron-platform" ]; then
    rm -rf "$HOME/Library/Application Support/twitch-desktop-electron-platform" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/twitch-desktop-electron-platform" ]; then
    rm -f "$HOME/Library/Application Support/twitch-desktop-electron-platform" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.twitch.spotlight.plist
echo "Removing $HOME/Library/Preferences/com.twitch.spotlight.plist..."
if [ -d "$HOME/Library/Preferences/com.twitch.spotlight.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.twitch.spotlight.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.twitch.spotlight.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.twitch.spotlight.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
