#!/bin/bash
# Uninstall script for Typinator
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Typinator..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Typinator if running..."
pkill -f "Typinator" 2>/dev/null || true

# Remove /Applications/Typinator.app
echo "Removing /Applications/Typinator.app..."
if [ -d "/Applications/Typinator.app" ]; then
    rm -rf "/Applications/Typinator.app" 2>/dev/null || true
elif [ -f "/Applications/Typinator.app" ]; then
    rm -f "/Applications/Typinator.app" 2>/dev/null || true
fi

# Remove $HOME/Desktop/Typinator Tutorial.rtfd
echo "Removing $HOME/Desktop/Typinator Tutorial.rtfd..."
if [ -d "$HOME/Desktop/Typinator Tutorial.rtfd" ]; then
    rm -rf "$HOME/Desktop/Typinator Tutorial.rtfd" 2>/dev/null || true
elif [ -f "$HOME/Desktop/Typinator Tutorial.rtfd" ]; then
    rm -f "$HOME/Desktop/Typinator Tutorial.rtfd" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Typinator
echo "Removing $HOME/Library/Application Support/Typinator..."
if [ -d "$HOME/Library/Application Support/Typinator" ]; then
    rm -rf "$HOME/Library/Application Support/Typinator" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Typinator" ]; then
    rm -f "$HOME/Library/Application Support/Typinator" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.macility.typinator2.plist
echo "Removing $HOME/Library/Preferences/com.macility.typinator2.plist..."
if [ -d "$HOME/Library/Preferences/com.macility.typinator2.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.macility.typinator2.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.macility.typinator2.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.macility.typinator2.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
