#!/bin/bash
# Uninstall script for UnicodeChecker
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling UnicodeChecker..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping UnicodeChecker if running..."
pkill -f "UnicodeChecker" 2>/dev/null || true

# Remove /Applications/UnicodeChecker.app
echo "Removing /Applications/UnicodeChecker.app..."
if [ -d "/Applications/UnicodeChecker.app" ]; then
    rm -rf "/Applications/UnicodeChecker.app" 2>/dev/null || true
elif [ -f "/Applications/UnicodeChecker.app" ]; then
    rm -f "/Applications/UnicodeChecker.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/UnicodeChecker
echo "Removing $HOME/Library/Application Support/UnicodeChecker..."
if [ -d "$HOME/Library/Application Support/UnicodeChecker" ]; then
    rm -rf "$HOME/Library/Application Support/UnicodeChecker" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/UnicodeChecker" ]; then
    rm -f "$HOME/Library/Application Support/UnicodeChecker" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/net.earthlingsoft.UnicodeChecker
echo "Removing $HOME/Library/Caches/net.earthlingsoft.UnicodeChecker..."
if [ -d "$HOME/Library/Caches/net.earthlingsoft.UnicodeChecker" ]; then
