#!/bin/bash
# Uninstall script for Unity Hub
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Unity Hub..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Unity Hub if running..."
pkill -f "Unity Hub" 2>/dev/null || true

# Kill application with bundle ID com.unity3d.unityhub if running
echo "Stopping application with bundle ID com.unity3d.unityhub if running..."
killall -9 "com.unity3d.unityhub" 2>/dev/null || true

# Remove /Applications/Unity Hub.app
echo "Removing /Applications/Unity Hub.app..."
if [ -d "/Applications/Unity Hub.app" ]; then
    rm -rf "/Applications/Unity Hub.app" 2>/dev/null || true
elif [ -f "/Applications/Unity Hub.app" ]; then
    rm -f "/Applications/Unity Hub.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/UnityHub
echo "Removing $HOME/Library/Application Support/UnityHub..."
if [ -d "$HOME/Library/Application Support/UnityHub" ]; then
    rm -rf "$HOME/Library/Application Support/UnityHub" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/UnityHub" ]; then
    rm -f "$HOME/Library/Application Support/UnityHub" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.unity3d.unityhub.helper.plist
echo "Removing $HOME/Library/Preferences/com.unity3d.unityhub.helper.plist..."
if [ -d "$HOME/Library/Preferences/com.unity3d.unityhub.helper.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.unity3d.unityhub.helper.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.unity3d.unityhub.helper.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.unity3d.unityhub.helper.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.unity3d.unityhub.plist
echo "Removing $HOME/Library/Preferences/com.unity3d.unityhub.plist..."
if [ -d "$HOME/Library/Preferences/com.unity3d.unityhub.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.unity3d.unityhub.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.unity3d.unityhub.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.unity3d.unityhub.plist" 2>/dev/null || true
fi

# Remove /Applications/Unity/Hub
echo "Removing /Applications/Unity/Hub..."
if [ -d "/Applications/Unity/Hub" ]; then
    rm -rf "/Applications/Unity/Hub" 2>/dev/null || true
elif [ -f "/Applications/Unity/Hub" ]; then
    rm -f "/Applications/Unity/Hub" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
