#!/bin/bash
# Uninstall script for Vellum
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Vellum..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Vellum if running..."
pkill -f "Vellum" 2>/dev/null || true

# Remove /Applications/Vellum.app
echo "Removing /Applications/Vellum.app..."
if [ -d "/Applications/Vellum.app" ]; then
    rm -rf "/Applications/Vellum.app" 2>/dev/null || true
elif [ -f "/Applications/Vellum.app" ]; then
    rm -f "/Applications/Vellum.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/co.180g.Vellum
echo "Removing $HOME/Library/Application Scripts/co.180g.Vellum..."
if [ -d "$HOME/Library/Application Scripts/co.180g.Vellum" ]; then
    rm -rf "$HOME/Library/Application Scripts/co.180g.Vellum" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/co.180g.Vellum" ]; then
    rm -f "$HOME/Library/Application Scripts/co.180g.Vellum" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/co.180g.Vellum
echo "Removing $HOME/Library/Containers/co.180g.Vellum..."
if [ -d "$HOME/Library/Containers/co.180g.Vellum" ]; then
    rm -rf "$HOME/Library/Containers/co.180g.Vellum" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/co.180g.Vellum" ]; then
    rm -f "$HOME/Library/Containers/co.180g.Vellum" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
