#!/bin/bash
# Uninstall script for VIA
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling VIA..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping VIA if running..."
pkill -f "VIA" 2>/dev/null || true

# Remove /Applications/VIA.app
echo "Removing /Applications/VIA.app..."
if [ -d "/Applications/VIA.app" ]; then
    rm -rf "/Applications/VIA.app" 2>/dev/null || true
elif [ -f "/Applications/VIA.app" ]; then
    rm -f "/Applications/VIA.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/VIA
echo "Removing $HOME/Library/Application Support/VIA..."
if [ -d "$HOME/Library/Application Support/VIA" ]; then
    rm -rf "$HOME/Library/Application Support/VIA" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/VIA" ]; then
    rm -f "$HOME/Library/Application Support/VIA" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/via-nativia
echo "Removing $HOME/Library/Application Support/via-nativia..."
if [ -d "$HOME/Library/Application Support/via-nativia" ]; then
    rm -rf "$HOME/Library/Application Support/via-nativia" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/via-nativia" ]; then
    rm -f "$HOME/Library/Application Support/via-nativia" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/VIA
echo "Removing $HOME/Library/Logs/VIA..."
if [ -d "$HOME/Library/Logs/VIA" ]; then
    rm -rf "$HOME/Library/Logs/VIA" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/VIA" ]; then
    rm -f "$HOME/Library/Logs/VIA" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.via.configurator.plist
echo "Removing $HOME/Library/Preferences/org.via.configurator.plist..."
if [ -d "$HOME/Library/Preferences/org.via.configurator.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.via.configurator.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.via.configurator.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.via.configurator.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
