#!/bin/bash
# Uninstall script for VNote
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling VNote..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping VNote if running..."
pkill -f "VNote" 2>/dev/null || true

# Remove /Applications/VNote.app
echo "Removing /Applications/VNote.app..."
if [ -d "/Applications/VNote.app" ]; then
    rm -rf "/Applications/VNote.app" 2>/dev/null || true
elif [ -f "/Applications/VNote.app" ]; then
    rm -f "/Applications/VNote.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/VNote
echo "Removing $HOME/Library/Application Support/VNote..."
if [ -d "$HOME/Library/Application Support/VNote" ]; then
    rm -rf "$HOME/Library/Application Support/VNote" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/VNote" ]; then
    rm -f "$HOME/Library/Application Support/VNote" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.vnotex.vnote.plist
echo "Removing $HOME/Library/Preferences/com.vnotex.vnote.plist..."
if [ -d "$HOME/Library/Preferences/com.vnotex.vnote.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.vnotex.vnote.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.vnotex.vnote.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.vnotex.vnote.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/VNote
echo "Removing $HOME/Library/Preferences/VNote..."
if [ -d "$HOME/Library/Preferences/VNote" ]; then
    rm -rf "$HOME/Library/Preferences/VNote" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/VNote" ]; then
    rm -f "$HOME/Library/Preferences/VNote" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
