#!/bin/bash
# Uninstall script for Warp
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Warp..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Warp if running..."
pkill -f "Warp" 2>/dev/null || true

# Remove /Applications/Warp.app
echo "Removing /Applications/Warp.app..."
if [ -d "/Applications/Warp.app" ]; then
    rm -rf "/Applications/Warp.app" 2>/dev/null || true
elif [ -f "/Applications/Warp.app" ]; then
    rm -f "/Applications/Warp.app" 2>/dev/null || true
fi

# Remove $HOME/.warp
echo "Removing $HOME/.warp..."
if [ -d "$HOME/.warp" ]; then
    rm -rf "$HOME/.warp" 2>/dev/null || true
elif [ -f "$HOME/.warp" ]; then
    rm -f "$HOME/.warp" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/dev.warp.Warp-Stable
echo "Removing $HOME/Library/Application Support/dev.warp.Warp-Stable..."
if [ -d "$HOME/Library/Application Support/dev.warp.Warp-Stable" ]; then
    rm -rf "$HOME/Library/Application Support/dev.warp.Warp-Stable" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/dev.warp.Warp-Stable" ]; then
    rm -f "$HOME/Library/Application Support/dev.warp.Warp-Stable" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/warp.log*
echo "Removing $HOME/Library/Logs/warp.log*..."
if [ -d "$HOME/Library/Logs/warp.log*" ]; then
    rm -rf "$HOME/Library/Logs/warp.log*" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/warp.log*" ]; then
    rm -f "$HOME/Library/Logs/warp.log*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/dev.warp.Warp-Stable.plist
echo "Removing $HOME/Library/Preferences/dev.warp.Warp-Stable.plist..."
if [ -d "$HOME/Library/Preferences/dev.warp.Warp-Stable.plist" ]; then
    rm -rf "$HOME/Library/Preferences/dev.warp.Warp-Stable.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/dev.warp.Warp-Stable.plist" ]; then
    rm -f "$HOME/Library/Preferences/dev.warp.Warp-Stable.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/dev.warp.Warp-Stable.savedState
echo "Removing $HOME/Library/Saved Application State/dev.warp.Warp-Stable.savedState..."
if [ -d "$HOME/Library/Saved Application State/dev.warp.Warp-Stable.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/dev.warp.Warp-Stable.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/dev.warp.Warp-Stable.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/dev.warp.Warp-Stable.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
