#!/bin/bash
# Uninstall script for Weasis
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Weasis..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Weasis if running..."
pkill -f "Weasis" 2>/dev/null || true

# Remove $HOME/.weasis
echo "Removing $HOME/.weasis..."
if [ -d "$HOME/.weasis" ]; then
    rm -rf "$HOME/.weasis" 2>/dev/null || true
elif [ -f "$HOME/.weasis" ]; then
    rm -f "$HOME/.weasis" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.weasis.launcher.savedState
echo "Removing $HOME/Library/Saved Application State/org.weasis.launcher.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.weasis.launcher.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.weasis.launcher.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.weasis.launcher.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.weasis.launcher.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
