echo "Unloading service net.whatroute.LaunchHelper..."
launchctl unload -w /Library/LaunchAgents/net.whatroute.LaunchHelper.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/net.whatroute.LaunchHelper.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/net.whatroute.LaunchHelper.plist 2>/dev/null || true

