# Kill application with bundle ID net.whatroute.whatroute2 if running
echo "Stopping application with bundle ID net.whatroute.whatroute2 if running..."
killall -9 "net.whatroute.whatroute2" 2>/dev/null || true

# Remove /Applications/WhatRoute.app
echo "Removing /Applications/WhatRoute.app..."
if [ -d "/Applications/WhatRoute.app" ]; then
    rm -rf "/Applications/WhatRoute.app" 2>/dev/null || true
elif [ -f "/Applications/WhatRoute.app" ]; then
