#!/bin/bash
# Uninstall script for WordService
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling WordService..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping WordService if running..."
pkill -f "WordService" 2>/dev/null || true

# Remove /Applications/WordService.app
echo "Removing /Applications/WordService.app..."
if [ -d "/Applications/WordService.app" ]; then
    rm -rf "/Applications/WordService.app" 2>/dev/null || true
elif [ -f "/Applications/WordService.app" ]; then
    rm -f "/Applications/WordService.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.grunenberg.WordService.plist
echo "Removing $HOME/Library/Preferences/org.grunenberg.WordService.plist..."
if [ -d "$HOME/Library/Preferences/org.grunenberg.WordService.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.grunenberg.WordService.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.grunenberg.WordService.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.grunenberg.WordService.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
