#!/bin/bash
# Uninstall script for XnViewMP
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling XnViewMP..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping XnViewMP if running..."
pkill -f "XnViewMP" 2>/dev/null || true

# Remove /Applications/XnViewMP.app
echo "Removing /Applications/XnViewMP.app..."
if [ -d "/Applications/XnViewMP.app" ]; then
    rm -rf "/Applications/XnViewMP.app" 2>/dev/null || true
elif [ -f "/Applications/XnViewMP.app" ]; then
    rm -f "/Applications/XnViewMP.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.xnview.XnView.savedState
echo "Removing $HOME/Library/Saved Application State/com.xnview.XnView.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.xnview.XnView.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.xnview.XnView.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.xnview.XnView.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.xnview.XnView.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
