#!/bin/bash
# Uninstall script for Yattee
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Yattee..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Yattee if running..."
pkill -f "Yattee" 2>/dev/null || true

# Remove /Applications/Yattee.app
echo "Removing /Applications/Yattee.app..."
if [ -d "/Applications/Yattee.app" ]; then
    rm -rf "/Applications/Yattee.app" 2>/dev/null || true
elif [ -f "/Applications/Yattee.app" ]; then
    rm -f "/Applications/Yattee.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/stream.yattee.app
echo "Removing $HOME/Library/Application Scripts/stream.yattee.app..."
if [ -d "$HOME/Library/Application Scripts/stream.yattee.app" ]; then
    rm -rf "$HOME/Library/Application Scripts/stream.yattee.app" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/stream.yattee.app" ]; then
    rm -f "$HOME/Library/Application Scripts/stream.yattee.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/stream.yattee.app
echo "Removing $HOME/Library/Containers/stream.yattee.app..."
if [ -d "$HOME/Library/Containers/stream.yattee.app" ]; then
    rm -rf "$HOME/Library/Containers/stream.yattee.app" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/stream.yattee.app" ]; then
    rm -f "$HOME/Library/Containers/stream.yattee.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
