#!/bin/bash
# Uninstall script for Zappy
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Zappy..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Zappy if running..."
pkill -f "Zappy" 2>/dev/null || true

# Kill application with bundle ID com.blackbeltlabs.Zappy if running
echo "Stopping application with bundle ID com.blackbeltlabs.Zappy if running..."
killall -9 "com.blackbeltlabs.Zappy" 2>/dev/null || true

# Remove /Applications/Zappy.app
echo "Removing /Applications/Zappy.app..."
if [ -d "/Applications/Zappy.app" ]; then
    rm -rf "/Applications/Zappy.app" 2>/dev/null || true
elif [ -f "/Applications/Zappy.app" ]; then
    rm -f "/Applications/Zappy.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.blackbeltlabs.Zappy
echo "Removing $HOME/Library/Application Support/com.blackbeltlabs.Zappy..."
if [ -d "$HOME/Library/Application Support/com.blackbeltlabs.Zappy" ]; then
    rm -rf "$HOME/Library/Application Support/com.blackbeltlabs.Zappy" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.blackbeltlabs.Zappy" ]; then
    rm -f "$HOME/Library/Application Support/com.blackbeltlabs.Zappy" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.blackbeltlabs.Zappy
echo "Removing $HOME/Library/Caches/com.blackbeltlabs.Zappy..."
if [ -d "$HOME/Library/Caches/com.blackbeltlabs.Zappy" ]; then
    rm -rf "$HOME/Library/Caches/com.blackbeltlabs.Zappy" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.blackbeltlabs.Zappy" ]; then
