fi

# Remove $HOME/Library/Preferences/com.blackbeltlabs.Zappy.plist
echo "Removing $HOME/Library/Preferences/com.blackbeltlabs.Zappy.plist..."
if [ -d "$HOME/Library/Preferences/com.blackbeltlabs.Zappy.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.blackbeltlabs.Zappy.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.blackbeltlabs.Zappy.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.blackbeltlabs.Zappy.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/zappy
echo "Removing $HOME/Library/zappy..."
if [ -d "$HOME/Library/zappy" ]; then
    rm -rf "$HOME/Library/zappy" 2>/dev/null || true
elif [ -f "$HOME/Library/zappy" ]; then
    rm -f "$HOME/Library/zappy" 2>/dev/null || true
fi

